import "./App.css";
import React, { useState } from "react";
import Box_player1 from "./components/Board/Box_player1.js";
import Box_player2 from "./components/Board/Box_player2.js";
import Keyboard from "./components/Keyboard/Keyboard";
import Timer from "./components/Timer/Timer";
import { Container, Row, Col } from "react-bootstrap";
import party from "party-js";
import Modal from "react-modal";

function App() {
  const [isOpen, setIsOpen] = useState(false);
  function toggleModal() {
    setIsOpen(!isOpen);
  }

  const onOpenModal = () => {
    setIsOpen(true);
  };
  return (
    <div class="App">
      <div class="App-window">
        <button id="btn" onClick={onOpenModal}>
          Open Modal
        </button>
        <Modal
          isOpen={isOpen}
          onRequestClose={toggleModal}
          contentLabel="My dialog"
          className="mymodal"
          overlayClassName="myoverlay"
          closeTimeoutMS={500}
        >
          <div id="modal" className="modal-content">
            <div>My modal dialog</div>
            <button className="close-modal-btn" onClick={toggleModal}>
              Close modal
            </button>
          </div>
        </Modal>

        <link
          href="https://fonts.googleapis.com/css2?family=Fredoka&display=swap"
          rel="stylesheet"
        ></link>
        <link
          rel="stylesheet"
          href="https://stackpath.bootstrapcdn.com/bootstrap/4.4.1/css/bootstrap-grid.min.css"
        />
        <div class="font-center"> Feudle </div>
        <hr class="solid" />
        <Container>
          <Row>
            <Col>
              <Timer />
            </Col>
            <Col sm={6}>
              <Box_player1 />
            </Col>
            <Col>
              <Box_player2 />
            </Col>
          </Row>
        </Container>
        <Keyboard />
      </div>
    </div>
  );
}

export default App;
